import { withRouter } from "next/router";
import Link from "next/link";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Container from "./container";
//   import { logout } from '../store/store'
import { logout } from "../redux/reducer";
const menuList = ["home", "playlist", "album", "artist", "search"];
const AppLayout = ({ children, router }) => {
  const { query: { query = "" } = {} } = router;
  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();
  const [currentIndex, setCurrentIndex] = useState("0");
  const [search, setSearch] = useState(query);
  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleOnSearch = () => {
    router.push(`/search?query=${search}`);
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  const UserDropDown = (
    <div>
      <div onClick={handleLogout}>登出</div>
    </div>
  );

  return (
    <div className="layout">
      <div className="sidebar">
        <Container>
          <div className="logo">
            <Link href="/">
              <div>
                <img className="logo-img" src="/public/logo.png" alt="" />
                <span className="logo-text">Soundify</span>
              </div>
            </Link>
          </div>

          <ul className="menu">
            {menuList.map((item, index) => {
              return (
                <li key="item">
                  <img src={`/${item}.svg`} alt="" />
                  <span>item</span>
                  {}
                </li>
              );
            })}
          </ul>
        </Container>
      </div>
      <div className="content">
        <Container>{children}</Container>
      </div>
      <div className="player">
        <Container>
          <div className="player-header">
            <Link href="/">
              <a>github</a>
            </Link>
          </div>
          <div className="player-cover">
            <div className="user">user</div>
          </div>
          <div className="player-control">
            <div className="user">user</div>
          </div>
          <div className="music-info">
            <div className="user">user</div>
          </div>
          <div className="player-next">
            <div className="user">user</div>
          </div>
        </Container>
      </div>
      <style jsx global>
        {`
          html,
          body {
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
              Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
              sans-serif;
          }

          * {
            box-sizing: border-box;
          }
        `}
      </style>
      <style jsx>
        {`
          .header-inner {
            display: flex;
            justify-content: space-between;
          }

          .header-left {
            display: flex;
            justify-content: flex-start;
          }

          :global(.icon-github) {
            display: block;
            padding-top: 10px;
            margin-right: 20px;
            color: #fff;
            font-size: 40px;
          }

          :global(.footer) {
            text-align: center;
          }
        `}
      </style>
    </div>
  );
};

export default withRouter(AppLayout);
