import React, { Component } from "react";
import styles from "../styles/discover.module.css";
import { BrowseFeatured } from "react-spotify-api";
class Discover extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div>
        <BrowseFeatured>
          {(playlists, loading, error) =>
            playlists ? (
              <>
                <ul className={styles.contentCoverList}>
                  {playlists.playlists.items.map((playlist) => (
                    <li key={item.id} className={styles.contentCoverContainer}>
                      <img
                        src={item.cover}
                        alt=""
                        className={styles.contentCover}
                      />
                      <div className={styles.contentTitle}>{item.album}</div>
                      {this.state.currentContent === index ? (
                        <div className={styles.contentSelector}></div>
                      ) : null}
                    </li>
                  ))}
                </ul>
              </>
            ) : null
          }
        </BrowseFeatured>
        <img src="/icons/more.svg" alt="" style={{ marginTop: "55px" }} />
      </div>
    );
  }
}

export default Discover;
