import { withRouter } from "next/router";
import Link from "next/link";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Sidebar from "./sidebar";
import Container from "./container";
//   import { logout } from '../store/store'
import { logout } from "../redux/reducer";
const menuList = ["Home", "Playlist", "Album", "Artist", "Search"];
const AppLayout = ({ children, router }) => {
  const { query: { query = "" } = {} } = router;
  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [search, setSearch] = useState(query);
  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleOnSearch = () => {
    router.push(`/search?query=${search}`);
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  const UserDropDown = (
    <div>
      <div onClick={handleLogout}>登出</div>
    </div>
  );

  return (
    <div className="layout">
      <Sidebar />
      <div className="content">
        <div className="content-header">{menuList[currentIndex]}</div>
        <div className="content-cover-list"></div>
        <div className="content-line"></div>
        <div className="content-list"></div>
      </div>
      <div className="player">
        <div className="player-header">
          <img className="add" src="/add.svg" alt="" />
          <div>
            <img className="avatar" src="/avatar.jpeg" alt="" />
            <span className="nickname">Troye Guo</span>
            <span className="triangle"></span>
          </div>
        </div>
        <div className="player-cover">
          <div className="user">user</div>
        </div>
        <div className="player-control">
          <div className="user">user</div>
        </div>
        <div className="music-info">
          <div className="user">user</div>
        </div>
        <div className="player-next">
          <div className="user">user</div>
        </div>
      </div>
      <style jsx global>
        {`
          html,
          body {
            padding: 0;
            margin: 0;
            font-family: "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell,
              Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
          }

          * {
            box-sizing: border-box;
          }

          li {
            list-style: none;
          }
        `}
      </style>
      <style jsx>
        {`
          .player {
            position: absolute;
            right: 0;
            top: 0;
            width: 335px;
            height: 100%;
          }
          .player-header {
            display: flex;
            justify-content: center;
          }
          .add {
            height: 31px;
            margin-top: 13px;
          }
          .avatar {
            width: 36px;
            height: 36px;
            background: rgba(0, 0, 0, 0);
            border-radius: 50%;
            opacity: 1;
            margin-left: 35px;
          }
          .triangle {
            width: 0;
            height: 0;
            border: 5px solid transparent;
            border-top-color: rgba(61, 63, 134, 1);
            position: relative;
            top: 3px;
            left: 5px;
          }
          .nickname {
            font-size: 14px;
            line-height: 80px;
            color: rgba(61, 63, 134, 1);
            margin-left: 12px;
            position: relative;
            bottom: 12px;
          }
        `}
      </style>
    </div>
  );
};

export default withRouter(AppLayout);
