import { withRouter } from "next/router";
import Link from "next/link";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Sidebar from "./sidebar";
import Container from "./container";
//   import { logout } from '../store/store'
import { logout } from "../redux/reducer";
const menuList = ["Home", "Playlist", "Album", "Artist", "Search"];
const AppLayout = ({ children, router }) => {
  const { query: { query = "" } = {} } = router;
  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [search, setSearch] = useState(query);
  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleOnSearch = () => {
    router.push(`/search?query=${search}`);
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  const UserDropDown = (
    <div>
      <div onClick={handleLogout}>登出</div>
    </div>
  );

  return (
    <div className="layout">
      <Sidebar />
      <div className="content">
        <div className="content-header">{menuList[currentIndex]}</div>
        <div className="content-cover-list"></div>
        <div className="content-line"></div>
        <div className="content-list"></div>
      </div>
      <div className="player">
        <div className="player-header">
          <Link href="/">
            <a>github</a>
          </Link>
        </div>
        <div className="player-cover">
          <div className="user">user</div>
        </div>
        <div className="player-control">
          <div className="user">user</div>
        </div>
        <div className="music-info">
          <div className="user">user</div>
        </div>
        <div className="player-next">
          <div className="user">user</div>
        </div>
      </div>
      <style jsx global>
        {`
          html,
          body {
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
              Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
              sans-serif;
          }

          * {
            box-sizing: border-box;
          }

          li {
            list-style: none;
          }
        `}
      </style>
      <style jsx>
        {`
          .sidebar {
            position: absolute;
            left: 0;
            top: 0;
            width: 169px;
            height: 100%;
            background-color: #4b4fd3;
            color: white;
          }
          .logo {
            width: 100%;
            padding: 33px 0px 15px;
            display: flex;
            justify-content: center;
            cursor: pointer;
          }
          .logo-img {
            width: 42px;
            height: 42px;
          }
          .logo-text {
            font-size: 20px;
            margin-left: 10px;
            position: relative;
            bottom: 10px;
          }
          .menu-list {
            height: 62px;
            position: relative;
            cursor: pointer;
          }
          .menu-text {
            font-size: 17px;
            margin-left: 10px;
            position: relative;
            bottom: 3px;
          }
          .menu-selector {
            width: 150px;
            height: 44px;
            background: rgba(255, 255, 255, 1);
            opacity: 0.15;
            border-radius: 12px;
            position: absolute;
            right: 10px;
            bottom: 28px;
          }
          .player {
            position: absolute;
            right: 0;
            top: 0;
            width: 335px;
            height: 100%;
          }
        `}
      </style>
    </div>
  );
};

export default withRouter(AppLayout);
