import React, { Component } from "react";
import { withRouter } from "next/router";

import songList from "../mock/songs";
import styles from "../styles/content.module.css";
import { UserPlaylists } from "react-spotify-api";

const menuList = ["Home", "Playlist", "Album", "Artist", "Search"];
class Content extends Component {
  state = { currentIndex: 0, currentContent: 0 };
  render() {
    return (
      <div>
        <div className={styles.content}>
          <div className={styles.contentHeader}>
            {menuList[this.state.currentIndex]}
          </div>

          <ul className={styles.contentCoverList}>
            {songList().map((item, index) => {
              return (
                <li key={item.id} className={styles.contentCoverContainer}>
                  <img
                    src={item.cover}
                    alt=""
                    className={styles.contentCover}
                  />
                  <div className={styles.contentTitle}>{item.album}</div>
                  {this.state.currentContent === index ? (
                    <div className={styles.contentSelector}></div>
                  ) : null}
                </li>
              );
            })}
          </ul>
          <img src="/icons/more.svg" alt="" style={{ marginTop: "55px" }} />
          <div className={styles.contentLine}></div>
          {
            <ul className={styles.contentListContainer}>
              <img
                src="/icons/play_with_shadow.svg"
                alt=""
                className={styles.playButtonShadow}
              />
              <div className={styles.songListContainer}>
                <UserPlaylists>
                  {(playlists) =>
                    playlists ? (
                      <div style={{ margin: 0, width: "100%" }}>
                        {playlists.items.map((playlist) => (
                          <div
                            key={playlist.id}
                            link={`/playlist/${playlist.id}`}
                            img={
                              playlist.images.length > 0
                                ? playlist.images[0].url
                                : null
                            }
                            content={playlist.name}
                            playSong={() =>
                              props.playSong(
                                JSON.stringify({
                                  context_uri: playlist.uri,
                                })
                              )
                            }
                          />
                        ))}
                      </div>
                    ) : null
                  }
                </UserPlaylists>
              </div>
            </ul>
          }
          {
            // <ul className={styles.contentListContainer}>
            //   <img
            //     src="/icons/play_with_shadow.svg"
            //     alt=""
            //     className={styles.playButtonShadow}
            //   />
            //   {songList().map((item, index) => {
            //     return (
            //       <li
            //         key={item.id}
            //         className={styles.contentList}
            //         onMouseOver={() => {
            //           setCurrentSong(index);
            //         }}
            //         onMouseLeave={() => {
            //           setCurrentSong();
            //         }}
            //       >
            //         <div>{index + 1}</div>
            //         <img
            //           src="/icons/favorite.svg"
            //           alt=""
            //           style={{ width: "18px" }}
            //         />
            //         <div>{item.song}</div>
            //         {currentSong === index ? (
            //           <>
            //             <img
            //               src="/icons/play.svg"
            //               alt=""
            //               style={{ opacity: 0.61, width: 30 }}
            //             />
            //             <img
            //               src="/icons/more_dot.svg"
            //               alt=""
            //               style={{ width: 20 }}
            //             />
            //           </>
            //         ) : null}
            //         <div>{item.artist}</div>
            //         <div>{item.album}</div>
            //       </li>
            //     );
            //   })}
            // </ul>
          }
        </div>
      </div>
    );
  }
}

export default withRouter(Content);
