import React, { Component } from "react";
import { UserTracks } from "react-spotify-api";
import styles from "../styles/track.module.css";
class Tracks extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    let Tracks = UserTracks;
    return (
      <div>
        <div className={styles.contentLine}></div>
        {
          <ul className={styles.contentListContainer}>
            <img
              src="/icons/play_with_shadow.svg"
              alt=""
              className={styles.playButtonShadow}
            />
            <div className={styles.songListContainer}>
              {this.props.tracks ? (
                <>
                  {this.props.tracks.map((track, index) => (
                    <li
                      key={track.track.id}
                      className={styles.contentList}
                      // onMouseOver={() => {
                      //   setCurrentSong(index);
                      // }}
                      // onMouseLeave={() => {
                      //   setCurrentSong();
                      // }}
                    >
                      <div>{index + 1}</div>
                      <img
                        src="/icons/favorite.svg"
                        alt=""
                        style={{ width: "18px" }}
                      />
                      <div>{track.track.name}</div>
                      <div>{track.track.artists[0].name}</div>
                      <div>{track.track.album.name}</div>
                    </li>
                  ))}
                </>
              ) : (
                <Tracks>
                  {(tracks) =>
                    tracks ? (
                      <>
                        {tracks.items.map((track, index) => (
                          <li
                            key={track.track.id}
                            className={styles.contentList}
                            // onMouseOver={() => {
                            //   setCurrentSong(index);
                            // }}
                            // onMouseLeave={() => {
                            //   setCurrentSong();
                            // }}
                          >
                            <div>{index + 1}</div>
                            <img
                              src="/icons/favorite.svg"
                              alt=""
                              style={{ width: "18px" }}
                            />
                            <div>{track.track.name}</div>
                            <div>{track.track.artists[0].name}</div>
                            <div>{track.track.album.name}</div>
                          </li>
                        ))}
                      </>
                    ) : null
                  }
                </Tracks>
              )}
            </div>
          </ul>
        }
      </div>
    );
  }
}

export default Tracks;
