import React, { Component } from "react";
import styles from "../styles/login.module.css";
const displayText = [
  {
    title: "Stream your music with Soundify",
    subtitle: "A third party client for spotify",
    slogan: "Get Started",
    href: "http://localhost:3000/login",
  },
  {
    title: "Proudly made with Next.js",
    subtitle: "A server-side-render React framework",
    slogan: "Next.js",
    href: "https://nextjs.org/",
  },
  {
    title: "Amazing tool from Troye Guo",
    subtitle: "Looking for a good job",
    slogan: "About Me",
    href: "http://102410.xyz",
  },
];
class Login extends Component {
  state = { currentIndex: 0 };
  handleClick = (index) => {
    this.setState({ currentIndex: index });
  };
  render() {
    const renderText = (index) => {
      return (
        <div>
          <div>{displayText[index].subtitle}</div>
          <div>{displayText[index].title}</div>
          <a
            href={displayText[index].href}
            target="_blank"
            rel="noopener noreferrer"
          >
            <div>
              <div>{displayText[index].slogan}</div>
            </div>
          </a>
        </div>
      );
    };
    const renderDot = () => {
      return [0, 1, 2].map((item) => {
        return (
          <div
            key={item}
            onClick={() => {
              this.setState({ currentIndex: item });
            }}
          ></div>
        );
      });
    };
    return (
      <div className="root">
        <div>
          <div className="image">
            {this.state.currentIndex === 0 ? (
              <img src="/images/bg0.png" alt="" />
            ) : this.state.currentIndex === 1 ? (
              <img src="/images/bg1.jpg" alt="" />
            ) : (
              <img src="/images/bg2.jpg" alt="" />
            )}
          </div>
          <div className="info-container">
            {this.state.currentIndex === 0 ? (
              <div className="first-screen">{renderText(0)}</div>
            ) : this.state.currentIndex === 1 ? (
              <div className="second-screen">{renderText(1)}</div>
            ) : (
              <div className="third-screen">{renderText(2)}</div>
            )}
          </div>
          <div>{renderDot()}</div>
        </div>
      </div>
    );
  }
}

export default Login;
