import React, { Component } from "react";
import styles from "../styles/home.module.css";
import { UserTracks } from "react-spotify-api";
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = { index: 0 };
  }
  handleClick = (index) => {
    this.setState({ index });
  };
  render() {
    return (
      <>
        <div className={styles.coverParent}>
          <div
            className={styles.contentCoverContainer}
            onClick={() => {
              this.handleClick(0);
            }}
          >
            <div className={styles.contentTitle}>All Songs</div>
            {this.state.index === 0 ? (
              <div className={styles.contentSelector}></div>
            ) : null}
          </div>
          <div
            className={styles.contentCoverContainer}
            onClick={() => {
              this.handleClick(1);
            }}
          >
            <div className={styles.contentTitle}>Recently Played</div>
            {this.state.index === 1 ? (
              <div className={styles.contentSelector}></div>
            ) : null}
          </div>
        </div>
        <div>
          <div className={styles.contentLine}></div>
          <img
            src="/icons/play_with_shadow.svg"
            alt=""
            className={styles.playButtonShadow}
          />
          {
            <ul className={styles.contentListContainer}>
              <div className={styles.songListContainer}>
                {this.state.index === 1 ? (
                  <>
                    {this.props.tracks.map((track, index) => (
                      <li key={track.played_at} className={styles.contentList}>
                        <div>{index + 1}</div>
                        <img
                          src="/icons/favorite.svg"
                          alt=""
                          style={{ width: "18px" }}
                        />
                        <img
                          src="/icons/play.svg"
                          alt=""
                          style={{ width: "27px", opacity: 0.4 }}
                        />
                        <div>{track.track.name}</div>
                        <div>{track.track.artists[0].name}</div>
                        <div>{track.track.album.name}</div>
                        <img
                          src="/icons/more.svg"
                          alt=""
                          style={{ width: "18px" }}
                        />
                      </li>
                    ))}
                  </>
                ) : (
                  <UserTracks>
                    {(tracks) =>
                      tracks ? (
                        <>
                          {tracks.items.map((track, index) => (
                            <li
                              key={track.track.id}
                              className={styles.contentList}
                            >
                              <div>{index + 1}</div>
                              <img
                                src="/icons/favorite.svg"
                                alt=""
                                style={{ width: "18px" }}
                              />
                              <div>{track.track.name}</div>
                              <div>{track.track.artists[0].name}</div>
                              <div>{track.track.album.name}</div>
                            </li>
                          ))}
                        </>
                      ) : null
                    }
                  </UserTracks>
                )}
              </div>
            </ul>
          }
        </div>
      </>
    );
  }
}

export default Home;
