import { withRouter } from "next/router";
import Link from "next/link";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Sidebar from "./sidebar";
import songList from "../mock/songs";
import Container from "./container";
//   import { logout } from '../store/store'
import { logout } from "../redux/reducer";
const menuList = ["Home", "Playlist", "Album", "Artist", "Search"];
const AppLayout = ({ children, router }) => {
  const { query: { query = "" } = {} } = router;
  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [search, setSearch] = useState(query);
  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleOnSearch = () => {
    router.push(`/search?query=${search}`);
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  const UserDropDown = (
    <div>
      <div onClick={handleLogout}>登出</div>
    </div>
  );

  return (
    <div className="layout">
      <Sidebar />
      <div className="content">
        <div className="content-header">{menuList[currentIndex]}</div>
        <div className="content-cover-list"></div>
        <div className="content-line"></div>
        <div className="content-list"></div>
      </div>
      <div className="player">
        <div className="player-header">
          <img className="add" src="/add.svg" alt="" />
          <div>
            <img className="avatar" src="/avatar.jpeg" alt="" />
            <span className="nickname">Troye Guo</span>
            <span className="triangle"></span>
          </div>
        </div>
        <div className="player-cover-container">
          <img className="player-cover" src="/album.jpg" alt="" />
          <div className="player-control">
            <img
              src="/favorite.svg"
              alt=""
              style={{ position: "absolute", left: 90, top: 149 }}
            />
            <img
              src="/add_to_playlist.svg"
              alt=""
              style={{ position: "absolute", left: 90, top: 34 }}
            />
            <img
              src="/next.svg"
              alt=""
              style={{ position: "absolute", left: 149, top: 91 }}
            />
            <img
              src="/prev.svg"
              alt=""
              style={{ position: "absolute", left: 34, top: 91 }}
            />
            <img
              src="/play.svg"
              alt=""
              style={{ position: "absolute", left: 76, top: 76 }}
            />
          </div>
        </div>
        <div className="music-info">
          <div className="song-name">You Need to Calm Down</div>
          <div className="artist-name">Taylor Swift</div>
        </div>
        <div className="player-button">
          <img src="/shuffle.svg" alt="" />
          <span className="player-button-text">Shuffle</span>
        </div>

        <div className="player-next">
          <div className="next-text">Next</div>
          <div className="next-list-container">
            <ul className="next-list">
              {songList().map((item) => {
                return (
                  <li key={item.id} className="next-list-item">
                    <div className="next-song">{item.song}</div>
                    <p
                      className="next-artist-name"
                      style={{
                        fontSize: "14px",
                        lineHeight: "20px",
                        color: "rgba(61, 63, 134, 0.61)",
                      }}
                    >
                      {item.artist}
                    </p>
                    <img src="/play.svg" alt="" className="next-button" />
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      </div>
      <style jsx global>
        {`
          html,
          body {
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto,
              Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
              sans-serif;
          }

          html,
          body,
          div,
          span,
          applet,
          object,
          iframe,
          h1,
          h2,
          h3,
          h4,
          h5,
          h6,
          p,
          blockquote,
          pre,
          a,
          abbr,
          acronym,
          address,
          big,
          cite,
          code,
          del,
          dfn,
          em,
          img,
          ins,
          kbd,
          q,
          s,
          samp,
          small,
          strike,
          strong,
          sub,
          sup,
          tt,
          var,
          b,
          u,
          i,
          center,
          dl,
          dt,
          dd,
          ol,
          ul,
          li,
          fieldset,
          form,
          label,
          legend,
          table,
          caption,
          tbody,
          tfoot,
          thead,
          tr,
          th,
          td,
          article,
          aside,
          canvas,
          details,
          embed,
          figure,
          figcaption,
          footer,
          header,
          hgroup,
          menu,
          nav,
          output,
          ruby,
          section,
          summary,
          time,
          mark,
          audio,
          video {
            margin: 0;
            padding: 0;
            border: 0;
            font-size: 100%;
            font: inherit;
            vertical-align: baseline;
          }

          /* HTML5 display-role reset for older browsers */
          article,
          aside,
          details,
          figcaption,
          figure,
          footer,
          header,
          hgroup,
          menu,
          nav,
          section {
            display: block;
          }

          body {
            line-height: 1;
          }

          ol,
          ul {
            list-style: none;
          }

          blockquote,
          q {
            quotes: none;
          }

          blockquote:before,
          blockquote:after,
          q:before,
          q:after {
            content: "";
            content: none;
          }

          table {
            border-collapse: collapse;
            border-spacing: 0;
          }

          a {
            text-decoration: none;
            -webkit-backface-visibility: hidden;
          }

          li {
            list-style: none;
          }

          html,
          body {
            width: 100%;
            height: 100%;
            overflow: hidden;
            user-select: none;
          }
        `}
      </style>
      <style jsx>
        {`
          .player {
            position: absolute;
            right: 0;
            top: 0;
            width: 335px;
            height: 100%;
          }
          .player-header {
            display: flex;
            justify-content: center;
          }
          .add {
            height: 31px;
            margin-top: 13px;
          }
          .avatar {
            width: 36px;
            height: 36px;
            background: rgba(0, 0, 0, 0);
            border-radius: 50%;
            opacity: 1;
            margin-left: 35px;
          }
          .triangle {
            width: 0;
            height: 0;
            border: 5px solid transparent;
            border-top-color: rgba(61, 63, 134, 1);
            position: relative;
            top: 3px;
            left: 10px;
          }
          .nickname {
            font-size: 14px;
            line-height: 80px;
            color: rgba(61, 63, 134, 1);
            margin-left: 12px;
            position: relative;
            bottom: 12px;
          }
          .player-cover-container {
            height: 210px;
          }
          .player-cover {
            width: 204px;
            height: 204px;
            background: rgba(0, 0, 0, 0);
            box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.15);
            opacity: 1;
            border-radius: 102px;
            position: relative;
            left: 70px;
          }
          .player-control {
            width: 204px;
            height: 204px;
            background: rgba(255, 255, 255, 0.66);
            border: 5px solid #4b4fd3;
            position: relative;
