import React, { Component } from "react";
import Router, { withRouter } from "next/router";
import Content from "../components/Content";
import * as actionTypes from "../redux/action";
import $axios from "../$axios";
import { connect } from "react-redux";
import { SpotifyApiContext, SpotifyApiAxiosContext } from "react-spotify-api";
import Discover from "../components/Discover";
import Tracks from "../components/Tracks";
import Layout from "../components/Layout";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = { index: 0 };
    this.token = null;
  }
  componentDidMount() {
    this.setState({
      token: localStorage.getItem("react-spotify-access-token"),
    });

    // console.log(this.props, "cdm");
  }
  handleClick = (index) => {
    this.setState({ index });
  };
  render() {
    // console.log(this.props, "user");

    return (
      <div className="content">
        {this.state.token ? (
          <SpotifyApiAxiosContext.Provider value={$axios}>
            <SpotifyApiContext.Provider value={this.state.token}>
              <div className="content-header">Home</div>
              <div
                className={
                  this.state.index === 0
                    ? "content-list-title active"
                    : "content-list-title"
                }
                onClick={() => {
                  handleClick(2);
                }}
              >
                All Songs
              </div>
              <div
                className={
                  this.state.index === 1
                    ? "content-list-title active"
                    : "content-list-title"
                }
                onClick={() => {
                  handleClick(1);
                }}
              >
                Recently Played
              </div>
              <Tracks />
            </SpotifyApiContext.Provider>
          </SpotifyApiAxiosContext.Provider>
        ) : null}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    user: state.current_user,
    backgroundImage: state.backgroundImage,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setUser: (user) => dispatch({ type: actionTypes.SET_USER, user }),
    fetchRecentlyPlayed: (options) =>
      dispatch(actionTypes.fetchRecentlyPlayed(options)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Home));
