import React, { Component } from "react";
import styles from "../styles/home.module.css";
import { UserTracks } from "react-spotify-api";
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = { index: 0 };
  }
  handleClick = (index) => {
    this.setState({ index });
    this.props.handleIndex(index);
  };
  render() {
    let Tracks = UserTracks;
    return (
      <div className={styles.coverParent}>
        <div
          className={styles.contentCoverContainer}
          onClick={() => {
            this.handleClick(0);
          }}
        >
          <div className={styles.contentTitle}>All Songs</div>
          {this.state.index === 0 ? (
            <div className={styles.contentSelector}></div>
          ) : null}
        </div>
        <div
          className={styles.contentCoverContainer}
          onClick={() => {
            this.handleClick(1);
          }}
        >
          <div className={styles.contentTitle}>Recently Played</div>
          {this.state.index === 1 ? (
            <div className={styles.contentSelector}></div>
          ) : null}
        </div>
      </div>
    );
  }
}

export default Home;
