import { withRouter } from "next/router";
import Link from "next/link";
import { useState } from "react";
import songList from "../mock/songs";
import { useSelector, useDispatch } from "react-redux";
import Sidebar from "./sidebar";
import Player from "./player";
//   import { logout } from '../store/store'
import { logout } from "../redux/reducer";
const menuList = ["Home", "Playlist", "Album", "Artist", "Search"];
const AppLayout = ({ children, router }) => {
  const { query: { query = "" } = {} } = router;
  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [search, setSearch] = useState(query);
  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleOnSearch = () => {
    router.push(`/search?query=${search}`);
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  const UserDropDown = (
    <div>
      <div onClick={handleLogout}>登出</div>
    </div>
  );

  return (
    <div className="layout">
      <Sidebar />
      <div className="content">
        <div className="content-header">{menuList[currentIndex]}</div>
        <ul className="content-cover-list">
          {songList().map((item) => {
            return (
              <li key={item.id} className="content-cover-container">
                <img src={item.cover} alt="" className="content-cover" />
                <div className="content-title">{item.album}</div>
              </li>
            );
          })}
        </ul>
        <div className="content-line"></div>
        <ul className="content-list">
          {songList().map((item) => {
            return (
              <li key={item.id}>
                <div>{item.song}</div>
                <div>{item.artist}</div>
                <div>{item.album}</div>
              </li>
            );
          })}
        </ul>
      </div>

      <Player />
      <style jsx global>
        {`
          html,
          body {
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto,
              Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
              sans-serif;
          }

          html,
          body,
          div,
          span,
          applet,
          object,
          iframe,
          h1,
          h2,
          h3,
          h4,
          h5,
          h6,
          p,
          blockquote,
          pre,
          a,
          abbr,
          acronym,
          address,
          big,
          cite,
          code,
          del,
          dfn,
          em,
          img,
          ins,
          kbd,
          q,
          s,
          samp,
          small,
          strike,
          strong,
          sub,
          sup,
          tt,
          var,
          b,
          u,
          i,
          center,
          dl,
          dt,
          dd,
          ol,
          ul,
          li,
          fieldset,
          form,
          label,
          legend,
          table,
          caption,
          tbody,
          tfoot,
          thead,
          tr,
          th,
          td,
          article,
          aside,
          canvas,
          details,
          embed,
          figure,
          figcaption,
          footer,
          header,
          hgroup,
          menu,
          nav,
          output,
          ruby,
          section,
          summary,
          time,
          mark,
          audio,
          video {
            margin: 0;
            padding: 0;
            border: 0;
            font-size: 100%;
            font: inherit;
            vertical-align: baseline;
          }

          /* HTML5 display-role reset for older browsers */
          article,
          aside,
          details,
          figcaption,
          figure,
          footer,
          header,
          hgroup,
          menu,
          nav,
          section {
            display: block;
          }

          body {
            line-height: 1;
          }

          ol,
          ul {
            list-style: none;
          }

          blockquote,
          q {
            quotes: none;
          }

          blockquote:before,
          blockquote:after,
          q:before,
          q:after {
            content: "";
            content: none;
          }

          table {
            border-collapse: collapse;
            border-spacing: 0;
          }

          a {
            text-decoration: none;
            -webkit-backface-visibility: hidden;
          }

          li {
            list-style: none;
          }

          html,
          body {
            width: 100%;
            height: 100%;
            overflow: hidden;
            user-select: none;
          }
        `}
      </style>
      <style jsx>
        {`
          .content {
            position: absolute;
            left: 169px;
            top: 0;
            width: calc(100% - 504px);
            height: 100%;
            margin: 20px;
          }
          .content-header {
            width: 158px;
            height: 53px;
            font-size: 45px;
            font-family: Helvetica;
            font-weight: bold;
            line-height: 53px;
            color: rgba(69, 69, 69, 1);
            opacity: 1;
          }
          .content-cover-container {
            float: left;
            margin: 20px;
          }
          .content-cover {
            background: rgba(0, 0, 0, 0);
            box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.2);
            opacity: 1;
            border-radius: 3px;
            width: 107px;
            height: 107px;
          }
        `}
      </style>
    </div>
  );
};

export default withRouter(AppLayout);
