import { withRouter } from "next/router";
import Link from "next/link";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Container from "./container";
//   import { logout } from '../store/store'
import { logout } from "../redux/reducer";
const menuList = ["home", "playlist", "album", "artist", "search"];
const AppLayout = ({ children, router }) => {
  const { query: { query = "" } = {} } = router;
  const user = useSelector((store) => store.user);
  const dispatch = useDispatch();
  const [currentIndex, setCurrentIndex] = useState("0");
  const [search, setSearch] = useState(query);
  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleOnSearch = () => {
    router.push(`/search?query=${search}`);
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  const UserDropDown = (
    <div>
      <div onClick={handleLogout}>登出</div>
    </div>
  );

  return (
    <div className="layout">
      <div className="sidebar">
        <div className="logo">
          <Link href="/">
            <div>
              <img className="logo-img" src="/logo.png" alt="" />
              <span className="logo-text">Soundify</span>
            </div>
          </Link>
        </div>

        <ul className="menu">
          {menuList.map((item, index) => {
            return (
              <li key={item}>
                <img src={`/${item}.svg`} alt="" />
                <span>{item}</span>
                {index === currentIndex ? (
                  <div className="menu-selector"></div>
                ) : null}
              </li>
            );
          })}
        </ul>
      </div>
      <div className="content">
        <div className="content-header">{menuList[currentIndex]}</div>
        <div className="content-cover-list"></div>
        <div className="content-line"></div>
        <div className="content-list"></div>
      </div>
      <div className="player">
        <div className="player-header">
          <Link href="/">
            <a>github</a>
          </Link>
        </div>
        <div className="player-cover">
          <div className="user">user</div>
        </div>
        <div className="player-control">
          <div className="user">user</div>
        </div>
        <div className="music-info">
          <div className="user">user</div>
        </div>
        <div className="player-next">
          <div className="user">user</div>
        </div>
      </div>
      <style jsx global>
        {`
          html,
          body {
            padding: 0;
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
              Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
              sans-serif;
          }

          * {
            box-sizing: border-box;
          }

          li {
            list-style: none;
          }
        `}
      </style>
      <style jsx>
        {`
          .sidebar {
            position: absolute;
            left: 0;
            top: 0;
            width: 169px;
            height: 100%;
            background-color: #4b4fd3;
            color: white;
          }
          .logo {
            width: 100%;
            padding: 33px 21px;
            display: flex;
            justify-content: center;
          }
          .logo-img {
            width: 42px;
            height: 42px;
          }
          .player {
            position: absolute;
            right: 0;
            top: 0;
            width: 335px;
            height: 100%;
          }
        `}
      </style>
    </div>
  );
};

export default withRouter(AppLayout);
