import React, { Component } from "react";
import Router, { withRouter, useRouter } from "next/router";
import Sidebar from "../components/Sidebar";
import Player from "../components/Player";
import Content from "../components/Content";
import * as actionTypes from "../redux/action";
import axios from "axios";
import { connect } from "react-redux";
import { SpotifyApiContext, SpotifyApiAxiosContext } from "react-spotify-api";
import { logger } from "../utils";
const menuList = ["Home", "Playlist", "Album", "Artist", "Search"];
class Index extends Component {
  state = {};
  componentDidMount() {
    // console.log(this.props, "cdm");
    axios.interceptors.response.use(
      (response) => {
        return response;
      },
      (error) => {
        // console.log(error.response, "error");
        const originalRequest = error.config;
        if (error.response.status === 401 && !originalRequest._retry) {
          originalRequest._retry = true;
          console.log("Error happened");
          console.log(error);
          // return window.location.reload();
          return axios
            .post("http://localhost:3000/refresh", {
              data: JSON.stringify({
                refresh_token: localStorage.getItem(
                  "react-spotify-refresh-token"
                ),
              }),
            })
            .then((res) => {
              console.log(this, "props");
              if (res.status === 200) {
                // 1) put token to LocalStorage
                localStorage.setItem(
                  "react-spotify-access-token",
                  res.data.access_token
                );
                logger.log("New token", res.data);
                logger.log(originalRequest);
                this.props.setUser({ access_token: res.data.access_token });

                // // 2) Change Authorization header
                // axios.defaults.headers.common['Authorization'] =
                //   'Bearer ' + localStorageService.getAccessToken();

                // 3) return originalRequest object with Axios.
                return axios(originalRequest);
              }
            });
        }
      }
    );

    let params = this.getHashParams();
    logger.log("Params", params);
    // If access token doesn't exist in has params, try to take it from local storage
    if (!("access_token" in params)) {
      const currentAccessToken = localStorage.getItem(
        "react-spotify-access-token"
      );

      if (currentAccessToken) {
        params.access_token = currentAccessToken;
      }
    }
    logger.log("Params after", params);
    if ("access_token" in params) {
      logger.log("access_token" in params);
      axios
        .get("https://api.spotify.com/v1/me", {
          headers: {
            Authorization: `Bearer ${params.access_token}`,
          },
        })
        .then(({ data }) => {
          localStorage.setItem(
            "react-spotify-access-token",
            params.access_token
          );
          if (params.refresh_token) {
            localStorage.setItem(
              "react-spotify-refresh-token",
              params.refresh_token
            );
          }
          let newUser = {
            access_token: params.access_token,
            displayName: data.display_name,
            email: data.email,
            id: data.id,
            type: data.type,
            country: data.country,
            product: data.product,
          };
          logger.log(this.props.setUser);
          this.setState({ loading: false });
          this.logInUserAndGetInfo(newUser);
          this.props.fetchRecentlyPlayed({ limit: 12 });
        })
        .catch((err) => {
          console.log(err, "fgjxfjxf");
          // 401 = Unauthorized - the access token is incorrect (expired)
          if (err.response.status === 401) {
            // Check if refresh token exists
            const refreshToken = localStorage.getItem(
              "react-spotify-refresh-token"
            );
            if (refreshToken) {
              // Send refresh token to server to acquire a new access token
              axios
                .post("http://localhost:3000/refresh", {
                  data: JSON.stringify({
                    refresh_token: refreshToken,
                  }),
                })
                .then((res) => {
                  logger.log("Refresh token response -", res.data);
                  axios
                    .get("https://api.spotify.com/v1/me", {
                      headers: {
                        Authorization: `Bearer ${res.data.access_token}`,
                      },
                    })
                    .then(({ data }) => {
                      localStorage.setItem(
                        "react-spotify-access-token",
                        res.data.access_token
                      );

                      let newUser = {
                        access_token: res.data.access_token,
                        displayName: data.display_name,
                        email: data.email,
                        id: data.id,
                        type: data.type,
                        country: data.country,
                        product: data.product,
                      };
                      this.logInUserAndGetInfo(newUser);
                      this.props.fetchRecentlyPlayed({ limit: 12 });
                    });
                })
                .catch((e) => {
                  logger.log("Refresh token error -", e);
                })
                .finally(() => {
                  this.setState({ loading: false });
                });
            } else {
              // Refresh token doesn't exist, the user is shown a 'login with Spotify button'
              this.setState({ loading: false });
            }
          }
        });
    } else {
      // The user is shown a 'login with Spotify button'
      this.setState({ loading: false });
    }
  }
  logInUserAndGetInfo = (newUser) => {
    logger.log("LOG IN", newUser);
    const router = useRouter();
    this.props.setUser(newUser); // set user in redux state
    if (router.pathname === "/") {
      Router.push("/browse/featured"); // if there is no page the user wants to go to
      // then go to the home page
    } else {
      // if there is a page the user wants to go to then just send them there
      Router.push();
    }
  };

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    logger.log(hashParams, "hashParams");
    return hashParams;
  }
  render() {
    // console.log(this.props, "user");
    return (
      <div className="layout">
        <SpotifyApiAxiosContext.Provider value={axios}>
          <SpotifyApiContext.Provider
          // value={this.props.user.access_token}
          >
            <Sidebar />
            <Content />
            <Player />
          </SpotifyApiContext.Provider>
        </SpotifyApiAxiosContext.Provider>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    user: state.current_user,
    backgroundImage: state.backgroundImage,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    setUser: (user) => dispatch({ type: actionTypes.SET_USER, user }),
    fetchRecentlyPlayed: (options) =>
      dispatch(actionTypes.fetchRecentlyPlayed(options)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Index));
